import { useEffect, useState } from 'react';
import '../src/App.css'
import LeftMenu from './components/leftMenu';
import { Logo } from './components/logo';
import Barcode from './components/barcode';
import Legic from './components/legic';
import Importar from './components/importar';
import Status from './components/status';
import Exportar from './components/exportar';

function App() {
  const [viewSelected, setViewSelected] = useState('logo');
  const [excelData, setExcelData] = useState(null);
  const [message, setMessage] = useState("");

  useEffect(() => {
    if (localStorage.getItem('gactiveLegicReadData')) {
      setMessage("Dados carregados do armazenamento local.");
    }
  }, []);

  function showMessage(tempMessage) {
    setMessage(tempMessage);
    setTimeout(() => setMessage(""), 3000);
  }

  function renderSection() {
    switch (viewSelected) {
      case 'barcode':
        return <Barcode excelData={excelData} showMessage={showMessage} />;
      case 'legic':
        return <Legic excelData={excelData} showMessage={showMessage} />;
      case 'status':
        return <Status />;
      case 'exportar':
        return <Exportar />;
      case 'importar':
        return <Importar setExcelData={setExcelData} showMessage={showMessage} />;
      default:
        return <Logo />;
    }
  }

  return (
    <div className="App">
      <LeftMenu onClick={setViewSelected} selected={viewSelected} />
      {message && <div className="message-box">{message}</div>}
      {renderSection()}
    </div>
  );
}

export default App;
