import React, { useState } from 'react';
import Barcode from './barcode/index';
import Exportar from './exportar/index';
import Importar from './importar/index';
import LeftMenu from './leftmenu/index';
import Legic from './legic/index';
import Limpar from './limpar/index';
import Logoinical from './logoinicial/index';
import Notification from './notification/index';
import Status from './status/index';

function App() {
  const [selected, setSelected] = useState('logoinical'); // Controla qual seção está visível
  const [excelData, setExcelData] = useState(null);
  const [legicDB, setLegicDB] = useState([]);
  const [totalEntries, setTotalEntries] = useState(0);
  const [excelTableData, setExcelTableData] = useState(false);

  const showMessage = (message) => {
    // Exibir a notificação (se necessário)
    alert(message);
  };

  const handleMenuClick = (section) => {
    setSelected(section);
  };

  return (
    <div className="app-container">
      <LeftMenu selected={selected} onClick={handleMenuClick} excelData={excelData} />

      {selected === 'logoinical' && <Logoinical />}
      {selected === 'barcode' && <Barcode excelData={excelData} legicDB={legicDB} setLegicDB={setLegicDB} totalEntries={totalEntries} />}
      {selected === 'exportar' && <Exportar />}
      {selected === 'importar' && <Importar setExcelData={setExcelData} setDataToFilter={setExcelTableData} setExcelTableData={setExcelTableData} showMessage={showMessage} setTotalEntries={setTotalEntries} />}
      {selected === 'legic' && <Legic excelData={excelData} legicDB={legicDB} setLegicDB={setLegicDB} />}
      {selected === 'limpar' && <Limpar />}
      {selected === 'status' && <Status />}

      {selected !== 'logoinical' && (
        <Notification type="success" message="Componente carregada com sucesso!" onClose={() => {}} />
      )}
    </div>
  );
}

export default App;
