import { useState } from 'react';

export default function Limpar() {
    const [isCleaning, setIsCleaning] = useState(false);
    const [viewSelected, setViewSelected] = useState('limpar'); // Inicialize com 'limpar' para exibir Limpar por padrão

    const limparDados = () => {
        setIsCleaning(true);

        // Limpar os dados do localStorage
        localStorage.removeItem('gactiveImportedData');
        localStorage.removeItem('legicCount');
        localStorage.removeItem('barcodeCount');
        localStorage.removeItem('gactiveLegicReadData');

        // Atraso para garantir que a limpeza seja feita antes de atualizar o estado
        setTimeout(() => {
            // Aqui, você pode forçar o recarregamento da página ou reconfigurar o estado
            window.location.reload();
        }, 500); // Atraso de meio segundo para simular limpeza
    };

    return (
        <section className="limpar-container">
            <button className="clean-button" onClick={limparDados}>
                {isCleaning ? "Limpando..." : "Limpar Tudo"}
            </button>
        </section>
    );
}
